def data_visualization(data_raw):
    import pandas as pd
    import numpy as np
    import json
    import plotly.graph_objects as go
    import plotly.io as pio





    # The data_raw contains the dataset as a DataFrame with index as environmental impact levels
    # and columns as technologies.
    df = data_raw.copy()

    x = df.columns.tolist()
    y = df.index.tolist()
    z = df.values

    fig = go.Figure(data=go.Heatmap(
        z=z,
        x=x,
        y=y,
        colorscale='Viridis',
        colorbar=dict(title='Value')
    ))

    fig.update_layout(
        title='Heatmap of Environmental Impact versus Technology Used',
        xaxis_title='Technology Used',
        yaxis_title='Environmental Impact'
    )

    fig_json = pio.to_json(fig)
    fig_dict = json.loads(fig_json)

    return fig_dict