def data_visualization(data_raw):
    import pandas as pd
    import numpy as np
    import json
    import plotly.graph_objects as go
    import plotly.io as pio





    # Create box plot with PaymentMode on x-axis and mean on y-axis
    fig = go.Figure()

    # Since the dataset has one mean value per PaymentMode, box plot will show single points
    # To visualize it meaningfully, we create a box plot with each PaymentMode having a single value
    # Plotly box plot needs list of values for each category, so we pass list with one element

    for pm in data_raw['PaymentMode']:
        val = data_raw.loc[data_raw['PaymentMode'] == pm, 'mean'].values
        fig.add_trace(go.Box(y=val, x=[pm]*len(val), name=pm, boxpoints='all', jitter=0))

    fig.update_layout(
        title="Box Plot of mean Across Different PaymentMode Categories",
        xaxis_title="PaymentMode",
        yaxis_title="mean"
    )

    fig_json = pio.to_json(fig)
    fig_dict = json.loads(fig_json)

    return fig_dict