def data_wrangler(data_list):
    import pandas as pd
    import numpy as np
    '''
    Wrangle the data provided in data_list.
    
    data_list: A list of one or more pandas data frames containing the raw data to be wrangled.
    '''


    # Ensure data_list is a list even if a single DataFrame is passed
    if not isinstance(data_list, list):
        data_list = [data_list]

    # According to the problem statement, all product quantity data is in the single 'main' dataset,
    # so we take the first dataframe for processing.
    main = data_list[0]

    # Step 1: Identify all product columns starting with 'Product_'
    product_columns = [col for col in main.columns if col.startswith('Product_')]

    # Step 2: Aggregate total quantity sold per product type by summing across all rows
    total_quantity_per_product = main[product_columns].sum()

    # Step 3: Reshape the aggregated series into a tidy DataFrame for easier comparison
    total_quantity_per_product = total_quantity_per_product.reset_index()
    total_quantity_per_product.columns = ['Product_Type', 'Total_Quantity_Sold']

    # Step 4: Sort by total quantity sold descending for clear comparison
    total_quantity_per_product = total_quantity_per_product.sort_values(by='Total_Quantity_Sold', ascending=False).reset_index(drop=True)

    # Return the final DataFrame containing total quantities sold per product type
    return total_quantity_per_product